import { Component } from "react";
import CadastradorCliente from "../cadastradores/cadastradorCliente";

type props = {
    titulo: string,
    tema: string,
    dadosClienteAtualizacao: any
}

type state = {
    dadosClienteAtualizacao: any
}

export default class FormularioCadastroCliente extends Component<props, state> {
    private nome: string
    private sobrenome: string
    private email: string
    private telefone: string
    private ddd: string
    private estado: string
    private cidade: string
    private rua: string
    private bairro: string
    private numero: string
    private codigoPostal: string
    private informacoesAdicionais: string

    constructor(props) {
        super(props)
        this.capturarNome = this.capturarNome.bind(this)
        this.capturarSobrenome = this.capturarSobrenome.bind(this)
        this.capturarEmail = this.capturarEmail.bind(this)
        this.capturarTelefone = this.capturarTelefone.bind(this)
        this.capturarDDD = this.capturarDDD.bind(this)
        this.capturarEstado = this.capturarEstado.bind(this)
        this.capturarCidade = this.capturarCidade.bind(this)
        this.capturarBairro = this.capturarBairro.bind(this)
        this.capturarRua = this.capturarRua.bind(this)
        this.capturarNumero = this.capturarNumero.bind(this)
        this.capturarCodigoPostal = this.capturarCodigoPostal.bind(this)
        this.capturarInformacoesAdicionais = this.capturarInformacoesAdicionais.bind(this)
        this.cadastrarCliente = this.cadastrarCliente.bind(this)
        this.submeterFormulario = this.submeterFormulario.bind(this)
        this.inicializaDadosClienteEmModoAtualizacao = this.inicializaDadosClienteEmModoAtualizacao.bind(this)
    }

    componentDidMount(): void {
        M.AutoInit();
    }

    componentWillMount(): void {
        this.setState({
            dadosClienteAtualizacao: this.props.dadosClienteAtualizacao
        });
    }

    inicializaDadosClienteEmModoAtualizacao() {
        const dadosCliente = this.props.dadosClienteAtualizacao
        if (this.props.dadosClienteAtualizacao != null && this.props.titulo == 'Alteração de Cliente') {
            this.nome = dadosCliente?.nome;
            this.sobrenome = dadosCliente?.sobreNome;
            this.email = dadosCliente?.email;
            this.telefone = dadosCliente?.telefone?.numero;
            this.ddd = dadosCliente?.telefone?.ddd;
            this.estado = dadosCliente?.estado;
            this.cidade = dadosCliente?.cidade;
            this.rua = dadosCliente?.rua;
            this.bairro = dadosCliente?.bairro;
            this.numero = dadosCliente?.numero;
            this.codigoPostal = dadosCliente?.codigoPostal;
            this.informacoesAdicionais = dadosCliente?.informacoesAdicionais;
            console.log(this.nome);
        }
    }

    public capturarNome(evento: any) {
        const nome = evento.target.value

        this.setState({
            dadosClienteAtualizacao: {
              [nome]: nome
            }
          });
    }

    public capturarSobrenome(evento: any) {
        this.sobrenome = evento.target.value
    }

    public capturarTelefone(evento: any) {
        this.telefone = evento.target.value
    }

    public capturarDDD(evento: any) {
        this.ddd = evento.target.value
    }

    public capturarEmail(evento: any) {
        this.email = evento.target.value
    }

    public capturarEstado(evento: any) {
        this.estado = evento.target.value
    }

    public capturarCidade(evento: any) {
        this.cidade = evento.target.value
    }

    public capturarBairro(evento: any) {
        this.bairro = evento.target.value
    }

    public capturarRua(evento: any) {
        this.rua = evento.target.value
    }

    public capturarNumero(evento: any) {
        this.numero = evento.target.value
    }

    public capturarCodigoPostal(evento: any) {
        this.codigoPostal = evento.target.value
    }

    public capturarInformacoesAdicionais(evento: any) {
        this.informacoesAdicionais = evento.target.value
    }

    public cadastrarCliente(objeto: Object) {
        let cadastrador = new CadastradorCliente()
        cadastrador.cadastrar(objeto)
    }

    public submeterFormulario(evento: any) {
        evento.preventDefault()

        if (this.props.titulo == 'Novo Cliente') {
            this.efetuaCadastro()
        } else {
            this.efetuaAtualizacao()
        }
        evento.target.reset()
    }

    efetuaCadastro() {
        let cliente = {
            nome: this.nome,
            sobreNome: this.sobrenome,
            email: this.email,
            telefone: {
                ddd: this.ddd,
                numero: this.telefone
            },
            endereco: {
                estado: this.estado,
                cidade: this.cidade,
                bairro: this.bairro,
                rua: this.rua,
                numero: this.numero,
                codigoPostal: this.codigoPostal,
                informacoesAdicionais: this.informacoesAdicionais
            }
        };
        console.log(cliente);
        this.cadastrarCliente(cliente);
        this.showToast('Cadastrado com sucesso!');
    }

    efetuaAtualizacao() {

    }

    render() {
        let estiloTitulo = `center-align blue-text`
        let titulo = `${this.props.titulo}`
        let estiloBotao = `btn waves-effect waves-light ${this.props.tema}`

        return (
            <>
                <h5 className={estiloTitulo}>{titulo}</h5>
                <div className="row">
                    <form className="col s12" onSubmit={(e) => this.submeterFormulario(e)}>
                        <div className="row">
                            <div className="input-field col s6">
                                <input id="nome" type="text" className="validate" value={this.state.dadosClienteAtualizacao.nome} onChange={(e) =>this.capturarNome(e)} />
                                <label className="active" htmlFor="nome">Nome</label>
                            </div>
                            <div className="input-field col s6">
                                <input id="sobrenome" type="text" className="validate" onChange={(e) =>this.capturarSobrenome(e)} />
                                <label className="active" htmlFor="sobrenome">Sobrenome</label>
                            </div>
                        </div>
                        <div className="row">
                            <div className="input-field col s1">
                                <input id="ddd" type="text" className="validate" onChange={(e) =>this.capturarDDD(e)} />
                                <label className="active" htmlFor="ddd">DDD</label>
                            </div>
                            <div className="input-field col s5">
                                <input id="telefone" type="text" className="validate" onChange={(e) =>this.capturarTelefone(e)} />
                                <label className="active" htmlFor="telefone">Telefone</label>
                            </div>
                            <div className="input-field col s6">
                                <input id="email" type="email" className="validate" onChange={(e) =>this.capturarEmail(e)} />
                                <label className="active" htmlFor="email">E-mail</label>
                            </div>
                        </div>
                        <div className="row">
                            <div className="input-field col s1">
                                <input id="numero" type="text" className="validate" onChange={(e) =>this.capturarNumero(e)} />
                                <label className="active" htmlFor="numero">Número</label>
                            </div>
                            <div className="input-field col s5">
                                <input id="bairro" type="text" className="validate" onChange={(e) =>this.capturarBairro(e)} />
                                <label className="active" htmlFor="bairro">Bairro</label>
                            </div>
                            <div className="input-field col s6">
                                <input id="rua" type="text" className="validate" onChange={(e) =>this.capturarRua(e)} />
                                <label className="active" htmlFor="rua">Rua</label>
                            </div>
                        </div>
                        <div className="row">
                            <div className="input-field col s6">
                                <input id="cidade" type="text" className="validate" onChange={(e) =>this.capturarCidade(e)} />
                                <label className="active" htmlFor="cidade">Cidade</label>
                            </div>
                            <div className="input-field col s6">
                                <select defaultValue="SP" id="estado" className="validate" onChange={(e) =>this.capturarEstado(e)}>
                                    <option value="AC">Acre</option>
                                    <option value="AL">Alagoas</option>
                                    <option value="AP">Amapá</option>
                                    <option value="AM">Amazonas</option>
                                    <option value="BA">Bahia</option>
                                    <option value="CE">Ceará</option>
                                    <option value="DF">Distrito Federal</option>
                                    <option value="ES">Espírito Santo</option>
                                    <option value="GO">Goiás</option>
                                    <option value="MA">Maranhão</option>
                                    <option value="MT">Mato Grosso</option>
                                    <option value="MS">Mato Grosso do Sul</option>
                                    <option value="MG">Minas Gerais</option>
                                    <option value="PA">Pará</option>
                                    <option value="PB">Paraíba</option>
                                    <option value="PR">Paraná</option>
                                    <option value="PE">Pernambuco</option>
                                    <option value="PI">Piauí</option>
                                    <option value="RJ">Rio de Janeiro</option>
                                    <option value="RN">Rio Grande do Norte</option>
                                    <option value="RS">Rio Grande do Sul</option>
                                    <option value="RO">Rondônia</option>
                                    <option value="RR">Roraima</option>
                                    <option value="SC">Santa Catarina</option>
                                    <option value="SP">São Paulo</option>
                                    <option value="SE">Sergipe</option>
                                    <option value="TO">Tocantins</option>
                                    <option value="EX">Estrangeiro</option>
                                </select>
                                <label className="active" htmlFor="estado">Estado</label>
                            </div>
                        </div>
                        <div className="row">
                            <div className="input-field col s6">
                                <input id="codigoPostal" type="text" className="validate" onChange={(e) =>this.capturarCodigoPostal(e)} />
                                <label className="active" htmlFor="codigoPostal">Código Postal</label>
                            </div>
                            <div className="input-field col s6">
                                <input id="informacoesAdicionais" type="text" className="validate" onChange={(e) =>this.capturarInformacoesAdicionais(e)} />
                                <label className="active" htmlFor="informacoesAdicionais">Informações Adicionais</label>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col s12">
                                <button className={estiloBotao} type="submit" name="action">Submit
                                    <i className="material-icons right">send</i>
                                </button>
                            </div>
                        </div>
                    </form >
                </div >
            </>
        )
    }

    showToast(mensagem: string) {
        M.toast({ html: mensagem })
    }
}
