import { Component } from "react";
import BarraNavegacao from "./barraNavegacao";
import FormularioCadastroCliente from "./formularioCadastroCliente";
import ListaCliente from "./listaCliente";
import ListaProdutoServico from "./listaProdutoServico";
import ListaConsumo from "./listaConsumo";

type state = {
    tela: string
}

export default class Roteador extends Component<{}, state> {
    constructor(props: {} | Readonly<{}>) {
        super(props)
        this.state = {
            tela: 'Clientes'
        }
        this.selecionarView = this.selecionarView.bind(this)
    }

    selecionarView(novaTela: string, evento: Event) {
        evento.preventDefault()
        console.log(novaTela);
        this.setState({
            tela: novaTela
        })
    }

    render() {
        let barraNavegacao = <BarraNavegacao seletorView={this.selecionarView} tema="blue lighten-2" botoes={['Clientes', 'Produtos/Serviços', 'Consumos']} />
        if (this.state.tela === 'Clientes') {
            return (
                <>
                    {barraNavegacao}
                    <ListaCliente titulo="Lista de Clientes" tema="blue lighten-2" />
                </>
            )
        } else if (this.state.tela === 'Produtos/Serviços') {
            return (
                <>
                    {barraNavegacao}
                    <ListaProdutoServico titulo="Lista de Produtos/Serviços" tema="blue lighten-2" />
                </>
            )

        } else if (this.state.tela === 'Consumos') {
            return (
                <>
                    {barraNavegacao}
                    <ListaConsumo titulo="Lista de Consumos" tema="blue lighten-2" />
                </>
            )
        }

    }
}
